changeSet.sql:
SELECT AddGeometryColumn('public', 'harvest', 'geom', 3067, 'POINT', 2);
SELECT AddGeometryColumn('public', 'game_observation', 'geom', 3067, 'POINT', 2);

UPDATE harvest set geom = ST_SetSRID(ST_MakePoint(longitude, latitude), 3067);
UPDATE game_observation set geom = ST_SetSRID(ST_MakePoint(longitude, latitude), 3067);

CREATE INDEX ndx_game_observation_geom ON game_observation USING gist (geom);
CREATE INDEX ndx_harvest_geom ON harvest USING gist (geom);

SELECT AddGeometryColumn('public', 'srva_event', 'geom', 3067, 'POINT', 2);
UPDATE srva_event set geom = ST_SetSRID(ST_MakePoint(longitude, latitude), 3067);
CREATE INDEX ndx_srva_event_geom ON srva_event USING gist (geom);


ALTER TABLE rhy_annual_statistics RENAME CONSTRAINT fk_rhy_annual_report_rhy TO fk_rhy_annual_statistics_rhy;

ALTER INDEX IF EXISTS rhy_annual_report_pkey RENAME TO rhy_annual_statistics_pkey;
ALTER INDEX IF EXISTS ndx_rhy_annual_report_rhy_year RENAME TO ndx_rhy_annual_statistics_rhy_year;


INSERT INTO rhy_annual_statistics_state_change_event (statistics_id, state, point_of_time, user_id)
SELECT ras.rhy_annual_statistics_id, 'IN_PROGRESS', ras.creation_time, ras.created_by_user_id
FROM rhy_annual_statistics ras;

INSERT INTO rhy_annual_statistics_state_change_event (statistics_id, state, point_of_time)
SELECT rhy_annual_statistics_id, 'UNDER_INSPECTION', coalesce(locked_time, modification_time)
FROM rhy_annual_statistics;


ALTER TABLE permit_decision_species_amount ADD CONSTRAINT ck_permit_decision_species_amount_dates
CHECK (begin_date <= end_date AND (begin_date2 IS NULL OR (end_date < begin_date2 AND begin_date2 <= end_date2)));

ALTER TABLE permit_decision_species_amount ADD CONSTRAINT ck_permit_decision_restriction_valid
CHECK ((restriction_type IS NULL AND restriction_amount IS NULL)
OR (restriction_type IS NOT NULL AND restriction_amount IS NOT NULL));

ALTER TABLE harvest DROP CONSTRAINT ck_harvest_amount_bounds;
ALTER TABLE harvest ADD CONSTRAINT ck_harvest_amount_bounds
  CHECK (amount > 0 AND amount < 10000);

ALTER TABLE game_observation DROP CONSTRAINT ck_game_observation_amount_bounds;
ALTER TABLE game_observation ADD CONSTRAINT ck_game_observation_amount_bounds
  CHECK (amount > 0 AND amount < 10000);

INSERT INTO harvest_permit_application_status(name) VALUES('HIDDEN');
UPDATE harvest_permit_application SET status='HIDDEN' where status='CANCELLED';
DELETE FROM harvest_permit_application_status WHERE name='CANCELLED';

-- Application
UPDATE harvest_permit_application hpa SET permit_holder_name = c.name_finnish, permit_holder_code = c.official_code, permit_holder_type = CASE WHEN c.subtype = 'PERSON' OR c.subtype = 'BUSINESS' OR c.subtype = 'RY' THEN c.subtype ELSE 'OTHER' END FROM (SELECT organisation_id, subtype, official_code, name_finnish FROM organisation WHERE organisation.organisation_type = 'CLUB') as c WHERE hpa.permit_holder_id IS NOT NULL AND hpa.permit_holder_id = c.organisation_id;
UPDATE harvest_permit_application hpa SET permit_holder_name = p.first_name || ' ' || p.last_name, permit_holder_code = null, permit_holder_type = 'PERSON' FROM (SELECT person_id, first_name, last_name FROM person) as p WHERE hpa.permit_holder_id IS NULL AND hpa.contact_person_id = p.person_id;
-- Decision
UPDATE permit_decision d SET permit_holder_name = c.name_finnish, permit_holder_code = c.official_code, permit_holder_type = CASE WHEN c.subtype = 'PERSON' OR c.subtype = 'BUSINESS' OR c.subtype = 'RY' THEN c.subtype ELSE 'OTHER' END FROM (SELECT organisation_id, subtype, official_code, name_finnish FROM organisation WHERE organisation.organisation_type = 'CLUB') as c WHERE d.permit_holder_id IS NOT NULL AND d.permit_holder_id = c.organisation_id;
UPDATE permit_decision d SET permit_holder_name = p.first_name || ' ' || p.last_name, permit_holder_code = null, permit_holder_type = 'PERSON' FROM (SELECT person_id, first_name, last_name FROM person) as p WHERE d.permit_holder_id IS NULL AND d.contact_person_id = p.person_id;
-- Permit
UPDATE harvest_permit hp SET permit_holder_name = c.name_finnish, permit_holder_code = c.official_code, permit_holder_type = CASE WHEN c.subtype = 'PERSON' OR c.subtype = 'BUSINESS' OR c.subtype = 'RY' THEN c.subtype ELSE 'OTHER' END FROM (SELECT organisation_id, subtype, official_code, name_finnish FROM organisation WHERE organisation.organisation_type = 'CLUB') as c WHERE hp.permit_holder_id IS NOT NULL AND hp.permit_holder_id = c.organisation_id AND hp.permit_type_code IN ('100', '190');
UPDATE harvest_permit hp SET permit_holder_name = p.first_name || ' ' || p.last_name, permit_holder_code = null, permit_holder_type = 'PERSON' FROM (SELECT person_id, first_name, last_name FROM person) as p WHERE hp.permit_holder_id IS NULL AND hp.original_contact_person_id = p.person_id AND hp.permit_type_code IN ('100', '190');

ALTER TABLE harvest_permit_application_species_amount
ADD CONSTRAINT ck_harvest_permit_application_species_amount_validity_years
CHECK (validity_years >= 0 AND validity_years <= 5);

UPDATE harvest_permit_application SET harvest_permit_category = 'MOOSELIKE' WHERE permit_type_code = '100';
UPDATE harvest_permit_application SET harvest_permit_category = 'MOOSELIKE_NEW' WHERE permit_type_code = '190';
UPDATE harvest_permit_application SET harvest_permit_category = 'BIRD' WHERE permit_type_code = '300';
