    CHECK ((expenses_included = FALSE AND hourly_expenses_unit IS NULL AND daily_allowance IS NULL) OR
           (expenses_included = TRUE AND hourly_expenses_unit IS NOT NULL AND daily_allowance IS NOT NULL));

