changeSet.createProcedure:
CREATE OR REPLACE FUNCTION update_zone_simple_geom()
RETURNS TRIGGER AS $$
BEGIN
  NEW.simple_geom = ST_Multi(ST_Transform(ST_SimplifyPreserveTopology(NEW.geom, 1), 4326));
  RETURN NEW;
END
$$ LANGUAGE 'plpgsql';

