ALTER TABLE harvest_permit_application_schedule ADD CONSTRAINT ck_harvest_permit_application_schedule
    CHECK ((active_override IS NOT NULL AND begin_time IS NULL AND end_time IS NULL) OR
           (active_override IS NULL AND begin_time IS NOT NULL AND end_time IS NOT NULL AND begin_time <= end_time));

